import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Nav from 'react-bootstrap/Nav';
import { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';


const Header = () => {
  //function for 'home' button only
  // const [isClicked, setIsClicked] = useState(false);

  // //function for 'About' button only
  // const [isOnAboutPage, setIsOnAboutPage] = useState(false);

  // //function for 'contact' button only
  // const [isOnContactPage, setIsOnContactPage] = useState(false);

  // //function for 'myProjects'
  // const [isOnProjects, setIsOnProjects] = useState(false);

  // //Allows the server to read what page is active
  // const location = useLocation();

  
// || location.pathname === "/projects" || location.pathname === "/about" || location.pathname === "/contact"
  // useEffect(() => {
  //   setIsClicked(location.pathname === '/home');
  //   setIsOnAboutPage(location.pathname === '/about');
  //   setIsOnContactPage(location.pathname === '/contact');
  //   setIsOnProjects(location.pathname === '/projects');
  // }, [location.pathname]);

  // if(isClicked === true && location.pathname === isClicked){
  //   setIsOnAboutPage(false);
  //   setIsOnContactPage(false);
  //   setIsOnProjects(false);
  // }

  // if(isOnAboutPage === true && location.pathname === isOnAboutPage){
  //   setIsClicked(false);
  //   setIsOnContactPage(false);
  //   setIsOnProjects(false);
  // }

  // if(isOnContactPage === true && location.pathname === isOnContactPage){
  //   setIsClicked(false);
  //   setIsOnAboutPage(false);
  //   setIsOnProjects(false);
  // }

  // if (isOnProjects === true && location.pathname === isOnProjects){
  //   isClicked(true);
  //   setIsOnAboutPage(false);
  //   setIsOnContactPage(false);
  // }

  const location = useLocation();

  const [isClicked, setIsClicked] = useState(location.pathname === '/home');
  const [isOnAboutPage, setIsOnAboutPage] = useState(location.pathname === '/about');
  const [isOnContactPage, setIsOnContactPage] = useState(location.pathname === '/contact');
  const [isOnProjects, setIsOnProjects] = useState(location.pathname === '/projects');

  const handleHomeClick = () => {
    setIsClicked(true);
    setIsOnAboutPage(false);
    setIsOnContactPage(false);
    setIsOnProjects(false);
  };

  const handleAboutClick = () => {
    setIsOnAboutPage(true);
    setIsClicked(false);
    setIsOnContactPage(false);
    setIsOnProjects(false);
  };

  const handleContactClick = () => {
    setIsOnContactPage(true);
    setIsClicked(false);
    setIsOnAboutPage(false);
    setIsOnProjects(false);
  };

  const handleProjectsClick = () => {
    setIsOnProjects(true);
    setIsClicked(false);
    setIsOnAboutPage(false);
    setIsOnContactPage(false);
  };

  useEffect(() => {
    setIsClicked(location.pathname === '/home');
    setIsOnAboutPage(location.pathname === '/about');
    setIsOnContactPage(location.pathname === '/contact');
    setIsOnProjects(location.pathname === '/projects');
  }, [location.pathname]);


  
  return (
    <>

     {[false].map((expand) => (
        <Navbar key={expand} expand={expand} className="transparent-nav">
          
          <Container fluid>
            

            <Navbar.Brand className="Logo-txt" style={{textTransform:'uppercase'}}>Brandon's Portfolio</Navbar.Brand>

            <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} className="navBlock" />

            <Navbar.Offcanvas
              id={`offcanvasNavbar-expand-${expand}`}
              aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
              placement="end"
              className="offCanvas-expand"
            >

              <Offcanvas.Header closeButton className="Offcanvas-header">

                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>

                  <Navbar.Text style={{textTransform:"uppercase"}}>

                    <div className="nav-head-txt">
                      More about me
                    </div>

                    </Navbar.Text>

                </Offcanvas.Title>

              </Offcanvas.Header>

              <Offcanvas.Body className="Off-canvas-body">
              <Nav.Link to="/" as={Link} className="navLink">Home</Nav.Link>
              <Nav.Link to="/about" as={Link} className="navLink" >About</Nav.Link>
              <Nav.Link to="/projects" as={Link} className="navLink">My Projects</Nav.Link>
              </Offcanvas.Body>

            </Navbar.Offcanvas>
      
              <Offcanvas.Body className="Off-canvas-body-nav">

              {isClicked === false ? ( <Nav.Link to="/" as={Link} onClick={handleHomeClick} className="navLink1">
        Home
      </Nav.Link>) : (
                <Nav.Link to="/" as={Link} onClick={() => setIsClicked(true)} className="nav-link">Home</Nav.Link>
              )}

              {isOnAboutPage === false ? (<Nav.Link to="/about" as={Link} onClick={handleAboutClick} className="navLink2">
        About
      </Nav.Link>) : (
                <Nav.Link to="/about" as={Link} onClick={() => isOnAboutPage(true)} className="nav-link" >About</Nav.Link>
              )}

              {isOnContactPage === false ? (<Nav.Link to="/contact" as={Link} onClick={handleContactClick} className="navLink3">Contact Me</Nav.Link>) : (
                <Nav.Link to="/contact" as={Link} onClick={() => setIsOnContactPage(true)} className="nav-link">Contact Me</Nav.Link>
              )}

              {isOnProjects === false ? (<Nav.Link to="/projects" as={Link} onClick={handleProjectsClick} className="navLink4">My Projects</Nav.Link>) : (
                <Nav.Link to="/projects" as={Link} onClick={() => isOnProjects(true)} className="nav-link">My Projects</Nav.Link>
              )}

              </Offcanvas.Body>

          </Container>

        </Navbar>
      ))}

    </>
  )
}

export default Header;
