import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Nav from 'react-bootstrap/Nav';
import { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';


const Header = () => {
  //function for 'home' button only
  const [isClicked, setIsClicked] = useState(false);

  //function for 'About' button only
  const [isOnAboutPage, setIsOnAboutPage] = useState(false);

  //function for 'About' button only
  const [isOnContactPage, setIsOnContactPage] = useState()

  //Allows the server to read what page is active
  const location = useLocation();
  
// || location.pathname === "/projects" || location.pathname === "/about" || location.pathname === "/contact"
  useEffect(() => {
    try {
      if (location.pathname === "/home"){
        setIsClicked(true);
      }
    } catch (error) {
      console.log(error)
    }
    try {
      if (location.pathname === "/about") {
        setIsOnAboutPage(true);
      }
    } catch (error) {
      console.error(error);
    }
  }, [location.pathname]);
  
  return (
    <>

     {[false].map((expand) => (
        <Navbar key={expand} expand={expand} className="transparent-nav">
          <Container fluid>
            

            <Navbar.Brand className="Logo-txt" style={{textTransform:'uppercase'}}>Brandon's Portfolio</Navbar.Brand>

            <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} className="navBlock" />
            <Navbar.Offcanvas
              id={`offcanvasNavbar-expand-${expand}`}
              aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
              placement="end"
              className="offCanvas-expand"
            >
              <Offcanvas.Header closeButton className="Offcanvas-header">
                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>
                  <Navbar.Text style={{textTransform:"uppercase"}}>
                    <div className="nav-head-txt">
                    More about me
                    </div>
                    </Navbar.Text>
                </Offcanvas.Title>
              </Offcanvas.Header>

              <Offcanvas.Body className="Off-canvas-body">
              <Nav.Link to="/" as={Link} className="navLink">Home</Nav.Link>
              <Nav.Link to="/about" as={Link} className="navLink" >About</Nav.Link>
              <Nav.Link to="/projects" as={Link} className="navLink">My Projects</Nav.Link>
              </Offcanvas.Body>
            </Navbar.Offcanvas>
      
              <Offcanvas.Body className="Off-canvas-body-nav">

              {isClicked === false ? (<Nav.Link to="/" as={Link} onClick={setIsClicked} className="navLink1">Home</Nav.Link>) : (
                <Nav.Link to="/" as={Link} onClick={setIsClicked} className="nav-link">Home</Nav.Link>
              )}

              {isOnAboutPage === false ? (<Nav.Link to="/about" as={Link} className="navLink2" >About</Nav.Link>) : (
                <Nav.Link to="/about" as={Link} className="nav-link" >About</Nav.Link>
              )}

              <Nav.Link to="/contact" as={Link} className="navLink4">Contact Me</Nav.Link>
              <Nav.Link to="/projects" as={Link} className="navLink3">My Projects</Nav.Link>
              </Offcanvas.Body>
          </Container>
        </Navbar>
      ))}

    </>
  )
}

export default Header;
